import { Component, OnInit } from '@angular/core';
import { ChartDataSets, ChartOptions, ChartType } from 'chart.js';
import { Color, Label } from 'ng2-charts';
import { ProjectService } from '../Services/project.service';// Adjust the import path as needed

@Component({
  selector: 'app-monthly-status',
  templateUrl: './monthly-status.component.html',
  styleUrls: ['./monthly-status.component.css']
})
export class MonthlyStatusComponent implements OnInit {
  projects: any[] = [];
  months: string[] = ['2024-01', '2024-02', '2024-03', '2024-04',]; // Example months, adjust as needed
  selectedProjectId: number | null = null;
  selectedMonth: string | null = null;

  lineChartData: ChartDataSets[] = [];
  lineChartLabels: Label[] = [];
  lineChartOptions: ChartOptions = {
    responsive: true,
  };
  lineChartLegend = true;
  lineChartType: ChartType = 'line';
  lineChartPlugins = [];

  constructor(private projectService: ProjectService) {}

  ngOnInit() {
    this.projectService.getAllProjects().subscribe(projects => {
      this.projects = projects;
    });
  }

  onProjectSelect(event: any) {
    this.selectedProjectId = event.target.value;
    this.fetchStatusHistory();
  }

  onMonthSelect(event: any) {
    this.selectedMonth = event.target.value;
    this.fetchStatusHistory();
  }

  fetchStatusHistory() {
    if (this.selectedProjectId && this.selectedMonth) {
      this.projectService.getStatusHistory(this.selectedProjectId, this.selectedMonth).subscribe(statusHistory => {
        this.updateChartData(statusHistory);
      });
    }
  }

  updateChartData(statusHistory: any[]) {
    this.lineChartLabels = statusHistory.map(status => status.date); // Assuming 'date' is a field in your status data
    this.lineChartData = [
      { data: statusHistory.map(status => status.value), label: 'Status' } // Assuming 'value' is the field for status value
    ];
  }
}
