import { Component, OnInit } from '@angular/core';
import { ProjectService } from '../Services/project.service';
import { Project } from '../Models/project.model';
import { StatusEnum } from '../enum';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-project-details',
  templateUrl: './project-details.component.html',
  styleUrls: ['./project-details.component.css']
})
export class ProjectDetailsComponent implements OnInit{
  id: number=0;
  project: Project = {

    name: '',
    lead: '',
    techStack: '',
    projectStatus: '',
    startdate: '',
    deadline: '',
    totalPeopleAllocated: 0,
    actualAllocationSOW: 0,
    adrItemDocumentation: StatusEnum.NOT_YET,
    c4Documentation: StatusEnum.NOT_YET,
    salesHandover: StatusEnum.NOT_YET,
    architectureHandover: StatusEnum.NOT_YET,
    decodersRepository: StatusEnum.NOT_YET,
    processAdoptionLatestVersion: StatusEnum.NOT_YET,
    projectKickoff: StatusEnum.NOT_YET,
    projectSharedDocumentRepository: StatusEnum.NOT_YET,
    currentStatus:'',
    status: []
  };
  constructor( private route:ActivatedRoute, private projectService: ProjectService, private router: Router) {}
  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.projectService.getProjectById(this.id).subscribe(data => {
      this.project = data;
    }, error => console.log(error));
}
goBack() {
  this.router.navigate(['/all-projects']); // Adjust the route to your "Project List" page
}
}

<div class="container mt-4">
    <h2 class="text-center title">Projects Details</h2>

    <div class="form-container">

    <div>
        <label><b>Project Name:   </b></label> {{project.name}}
    </div>
    <div>
        <label><b>Project Lead: </b></label> {{project.lead}}
    </div>
    <div>
        <label><b>Tech Stack: </b></label> {{project.techStack}}
    </div>
    <div>
        <label><b>Start Date:</b></label> {{project.startdate}}
    </div>
    <div>
        <label><b>Deadline: </b></label> {{project.deadline}}
    </div>
    <div>
        <label><b>Status: </b></label> {{project.status}}
</div>
    <div>
        <label><b>Total people allocated: </b></label> {{project.totalPeopleAllocated}}
    </div>
    <div>
        <label><b>Actual allocation based on SOW:</b></label> {{project.actualAllocationSOW}}
    </div>
    <div>
        <label><b>Adr item documentation: </b></label> {{project.adrItemDocumentation}}
    </div>
    <div>
        <label><b>C4 documentation: </b></label> {{project.c4Documentation}}
    </div>
    <div>
        <label><b>Architechture handover:</b></label> {{project.architectureHandover}}
    </div>
    <div>
        <label><b>Decoders repository:</b></label> {{project.decodersRepository}}
    </div>
    <div>
        <label><b>Process adoption:</b></label> {{project.processAdoptionLatestVersion}}
    </div>
    <div>
        <label><b>Project kickoff:</b></label> {{project.projectKickoff}}
    </div>
    <div>
        <label><b>Project shared repository: </b></label> {{project.projectSharedDocumentRepository}}
    </div>
    <div>
        <label><b>Sales handover: </b></label> {{project.salesHandover}}
    </div><br>
    <button type="button" class="btn btn-secondary" (click)="goBack()">Back</button>

    </div>
    </div>
