import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';


@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  // Bar chart configuration
  barChartOptions = {
    responsive: true,
  };
  barChartLabels: string[] = [];
  barChartData: any[] = [];
  barChartType = 'bar';
  barChartLegend = true;

  // Pie chart configuration
  pieChartOptions = {
    responsive: true,
  };
  pieChartLabels: string[] = [];
  pieChartData: any[] = [];
  pieChartType = 'pie';
  pieChartLegend = true;

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.fetchProjectData();
  }

  fetchProjectData(): void {
    // Replace with your API endpoint
    this.http.get<any[]>('https://api.example.com/projects').subscribe(data => {
      this.updateCharts(data);
    });
  }

  updateCharts(data: any[]): void {
    this.barChartLabels = data.map(project => project.name);
    this.pieChartLabels = this.barChartLabels;

    const statuses = ['On Track', 'Slow', 'Risk', 'Deep Risk'];
    const statusColors = {
      'On Track': 'green',
      'Slow': 'yellow',
      'Risk': 'brown',
      'Deep Risk': 'red'
    };

    const barData = statuses.map(status => ({
      data: data.filter(project => project.status === status).map(project => project.name),
      label: status,
      backgroundColor: statusColors[status]
    }));

    const pieData = statuses.map(status => ({
      data: data.filter(project => project.status === status).length,
      label: status,
      backgroundColor: statusColors[status]
    }));

    this.barChartData = [{ data: data.map(project => statuses.indexOf(project.status)), label: 'Project Status' }];
    this.pieChartData = pieData;
  }
}
