import { Component } from '@angular/core';

@Component({
  selector: 'app-weekly-status',
  templateUrl: './weekly-status.component.html',
  styleUrls: ['./weekly-status.component.css']
})
export class WeeklyStatusComponent {
  projectName: string;
  month: number;
  year: number;
  chartData: any;

  constructor(private http: HttpClient) {}

  fetchWeeklyStatus() {
    const projectId = this.getProjectIdByName(this.projectName); // Add your logic to get projectId by name

    if (projectId) {
      const url = `http://localhost:8080/projects/${projectId}/weekly-status-by-month?month=${this.month}&year=${this.year}`;

      this.http.get<any[]>(url).subscribe(data => {
        this.chartData = data;
        this.renderBarChart();
      });
    }
  }

  renderBarChart() {
    const weekNumbers = this.chartData.map(d => d.weekNumber);
    const statuses = this.chartData.map(d => d.currentStatus);

    new Chart('barChart', {
      type: 'bar',
      data: {
        labels: weekNumbers,
        datasets: [{
          label: 'Current Status',
          data: statuses.map(status => this.getStatusValue(status)),
          backgroundColor: statuses.map(status => this.getStatusColor(status)),
        }]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true,
            ticks: {
              callback: (value) => this.getStatusLabel(value),
            }
          }
        },
        plugins: {
          tooltip: {
            callbacks: {
              label: (context) => this.getStatusLabel(context.raw),
            }
          }
        }
      }
    });
  }

  getStatusValue(status: string): number {
    const statusMap = { 'On Track': 1, 'Slow': 2, 'Risk': 3, 'Deep Risk': 4 };
    return statusMap[status] || 0;
  }

  getStatusLabel(value: number): string {
    const labelMap = { 1: 'On Track', 2: 'Slow', 3: 'Risk', 4: 'Deep Risk' };
    return labelMap[value] || 'Unknown';
  }

  getStatusColor(status: string): string {
    const colorMap = { 'On Track': 'green', 'Slow': 'orange', 'Risk': 'yellow', 'Deep Risk': 'red' };
    return colorMap[status] || 'gray';
  }

  getProjectIdByName(name: string): number {
    // Implement the logic to get project ID by name
    // This could be an API call or a local lookup
    return 15; // example
  }
}
}
