  onSubmit() {
    console.log('Project data:', this.project);
    this.projectService.saveProject(this.project).subscribe(
      response => {
        console.log('Project created:', response);

        // Assuming a successful response status or certain response content
        if (response === 'success') { // Adjust condition as needed
          this.successMessage = 'Project added successfully!';
        } 
      },
    );
  }
  goBack() {
    this.router.navigate(['/all-projects']); // Adjust the route to your "Project List" page
  }

}


