import { Component, OnInit } from '@angular/core';
import { ProjectService } from '../Services/project.service';  // Update the path if necessary
import { Chart, ChartOptions, ChartType } from 'chart.js';
import { Color, Label } from 'ng2-charts';
  // Importing configuration for Chart.js

@Component({
  selector: 'app-monthly-status',
  templateUrl: './monthly-status.component.html',
  styleUrls: ['./monthly-status.component.css']
})
export class MonthlyStatusComponent implements OnInit {
  projects: any[] = [];
  months = [
    { value: '2024-01', label: 'January 2024' },
    { value: '2024-02', label: 'February 2024' },
    { value: '2024-03', label: 'March 2024' },
    { value: '2024-04', label: 'April 2024' },
    { value: '2024-05', label: 'May 2024' },
    { value: '2024-06', label: 'June 2024' },
    { value: '2024-07', label: 'July 2024' },
    { value: '2024-08', label: 'August 2024' },
    { value: '2024-09', label: 'September 2024' },
    { value: '2024-10', label: 'October 2024' },
    { value: '2024-11', label: 'November 2024' },
    { value: '2024-12', label: 'December 2024' }
  ];
  selectedProject: number | null = null;  // Option 2: Initialized with null
  selectedMonth: string | null = null;
  chart: Chart | undefined;

  constructor(private projectService: ProjectService) { }

  ngOnInit(): void {
    this.loadProjects();
  }

  loadProjects(): void {
    this.projectService.getAllProjects().subscribe((projects: any[]) => {
      this.projects = projects;
    });
  }
  onProjectSelect(event: Event): void {
    this.selectedProject = +(event.target as HTMLSelectElement).value;
    this.loadStatusHistory();
  }

  onMonthChange(event: Event): void {
    this.selectedMonth = (event.target as HTMLInputElement).value;
    this.loadStatusHistory();
  }

  loadStatusHistory(): void {
    if (this.selectedProject && this.selectedMonth) {
      this.projectService.getStatusHistory(this.selectedProject, this.selectedMonth).subscribe(data => {
        console.log(data);  // Log the received data
        this.updateChart(data);
      });
    }
  }
  updateChart(data: any[]): void {

    const yearMonth = this.selectedMonth.split('-');
    const year = parseInt(yearMonth[0], 10);
    const month = parseInt(yearMonth[1], 10);

    // Create an array with all days in the month
    const daysInMonth = new Date(year, month, 0).getDate();
    const labels = Array.from({ length: daysInMonth }, (_, i) => `Day ${i + 1}`);

    // Initialize all days with default status (e.g., On Track)
    const statusData = Array(daysInMonth).fill(0);  // Assuming 0 = On Track

    // Map backend status data to the correct day
    data.forEach(entry => {
      const day = new Date(entry[1]).getDate() - 1;  // Assuming entry[1] contains the date
      const status = this.mapStatusToNumber(entry[0]); // Map status to a number
      statusData[day] = status;
    });

    if (this.chart) {
      this.chart.destroy();
    }

    this.chart = new Chart('lineChart', {
      type: 'line' as ChartType,
      data: {
        labels: labels,
        datasets: [
          {
            label: 'Current Status',
            data: statusData,
            fill: false,
            borderColor: 'blue',
            borderWidth: 2,
            cubicInterpolationMode: 'monotone',
          }
        ]
      },
      options: {
        responsive: true,
        scales: {
          x: {
            title: {
              display: true,
              text: 'Days'
            }
          },
          y: {
            title: {
              display: true,
              text: 'Status'
            },
            min: 0,
            max: 3,  // Adjust this based on your status values
            ticks: {
              callback: function(value: number) {
                const statuses = ['On Track', 'Slow', 'Risk', 'Deep Risk'];
                return statuses[value];
              }
            }
          }
        }
      } as ChartOptions
    });
  }

  mapStatusToNumber(status: string): number {
    switch (status) {
      case 'On Track':
        return 0;
      case 'Slow':
        return 1;
      case 'Risk':
        return 2;
      case 'Deep Risk':
        return 3;
      default:
        return 0;  // Default to "On Track"
    }
  }
}
