  this.status = {
    id: 0,
    risksInDelivery: false,
    teamComposition: {},
    processFlows: StatusEnum.NOT_YET,
    defectsReport: false,
    numberOfDefects: 0,
    dailyUpdates: StatusEnum.NOT_YET,
