    weeklyReports: StatusEnum.NOT_YET,
    closeClientCommunication: StatusEnum.NOT_YET,
    gitReport: StatusEnum.NOT_YET,
    escalation: StatusEnum.NOT_YET,
    boardUpdates: StatusEnum.NOT_YET
  };
  this.teamCompositionString = '';
}
goBack() {
