    <div>
        <label><b>Start Date:</b></label> {{project.startdate}}
    </div><br>
    <div>
        <label><b>Deadline: </b></label> {{project.deadline}}
    </div><br>
    <div>
        <label><b>Status: </b></label> {{project.status}}
</div><br>
    <div>
        <label><b>Total people allocated: </b></label> {{project.totalPeopleAllocated}}
    </div><br>
    <div>
        <label><b>Actual allocation based on SOW:</b></label> {{project.actualAllocationSOW}}
    </div><br>
    <div>
        <label><b>Adr item documentation: </b></label> {{project.adrItemDocumentation}}
    </div><br>
    <div>
        <label><b>C4 documentation: </b></label> {{project.c4Documentation}}
    </div><br>
    <div>
        <label><b>Architechture handover:</b></label> {{project.architectureHandover}}
    </div><br>
    <div>
        <label><b>Decoders repository:</b></label> {{project.decodersRepository}}
    </div><br>
    <div>
        <label><b>Process adoption:</b></label> {{project.processAdoptionLatestVersion}}
