import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { ChartOptions, ChartType, ChartData } from 'chart.js';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  barChartLabels: string[] = [];
  barChartData: any[] = [];
  pieChartLabels: string[] = [];
  pieChartData: any[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.fetchProjectData();
  }

  fetchProjectData(): void {
    // Replace with your API endpoint
    this.http.get<any[]>('http://localhost:8080/projects').subscribe(data => {
      this.updateCharts(data);
    });
  }

  updateCharts(data: any[]): void {
    this.barChartLabels = data.map(project => project.name);
    this.pieChartLabels = ['On Track', 'Slow', 'Risk', 'Deep Risk'];

    const statusCount = {
      'On Track': 0,
      'Slow': 0,
      'Risk': 0,
      'Deep Risk': 0
    };

    // Calculate counts for each status
    data.forEach(project => {
      statusCount[project.status]++;
    });

    // Bar chart data
    this.barChartData = [
      { data: data.map(project => this.pieChartLabels.indexOf(project.status)), label: 'Project Status', backgroundColor: ['green', 'yellow', 'brown', 'red'] }
    ];

    // Pie chart data
    this.pieChartData = [
      {
        data: Object.values(statusCount),
        backgroundColor: ['green', 'yellow', 'brown', 'red'],
        label: 'Status Distribution'
      }
    ];
  }
}
